import React from 'react';
import ReactDOM from 'react-dom';

const scaleNames = {
    c: 'Celsius',
    f: 'Fahrenheit'
}

/*
T(°F) = T(°C) × 9/5 + 32

T(°C) = (T(°F) - 32) × 5/9
 */

function toCelsius (fahrenheit) {
    return (fahrenheit - 32) * 5 / 9
}

function toFahrenheit (celsius) {
    return (celsius * 9 / 5) + 32
}

function BoilingVerdict({celsius}) {
    if (celsius >= 100) {
        return <div className="alert alert-success">L'eau bout</div>
    }
    return <div className="alert alert-info">L'eau ne bout pas</div>
}

function tryConvert (temperature, convert) {
    const value = parseFloat(temperature)
    if (Number.isNaN(value)) {
        return '';
    }
    const output = (Math.round(convert(value) * 100) / 100).toString()
}

class TemperatureInput extends React.Component {

    constructor(props) {
        super(props)
        this.handleChange = this.handleChange.bind(this)
    }

    handleChange (e) {
        this.props.onTemperatureChange(e.target.value)
    }

    render () {
        const temperature = this.props.temperature
        const name = 'scale' + this.props.scale
        const scaleName = scaleNames[this.props.scale]
        return (
            <div className="form-group">
                <label htmlFor={ name }>Temperature (en { scaleName })</label>
                <input onChange={ this.handleChange } type="text" id={ name } value={ temperature } className="form-control"/>
            </div>
        )
    }
}

class Calculator extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            temperature: 20,
            scale: 'c'
        }
        this.handleFahrenheitChange = this.handleFahrenheitChange.bind(this)
        this.handleCelsiusChange = this.handleCelsiusChange.bind(this)
    }

    handleCelsiusChange (temperature) {
        this.setState({
            scale: 'c',
            temperature
        })
    }

    handleFahrenheitChange (temperature) {
        this.setState({
            scale: 'f',
            temperature
        })
    }

    render () {
        const {temperature, scale} = this.state
        const celsius = scale === 'c' ? temperature : tryConvert(temperature ,toCelsius)
        const fahrenheit = scale === 'f' ? temperature : tryConvert(temperature, toFahrenheit)
        return (
        <div className="mt-5">
            <TemperatureInput scale="c" temperature={ celsius } onTemperatureChange={ this.handleCelsiusChange }/>
            <TemperatureInput scale="f" temperature={ fahrenheit } onTemperatureChange={ this.handleFahrenheitChange }/>
            <BoilingVerdict celsius={ celsius } />
        </div>
        )
    }
}

ReactDOM.render (<Calculator />,
    document.getElementById('app'));
